import React, { Component } from "react";
import {
  Grid,
  Row,
  Col,
  Button,
  FormGroup,
  ControlLabel,
  FormControl,
  HelpBlock
} from "semantic-ui-react";
import CalendarHeatmap from "react-calendar-heatmap";
import ReactTooltip from "react-tooltip";
import Modal from "react-responsive-modal";

import "./../assets/Main.css";

const today = new Date();

class Calendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      show: false,
      value: "",
      startDate: this.getStartDate(),
      endDate: this.getEndDate(),
      values: this.getValues()
    };
    this.openModal = this.openModal.bind(this);
    this.closeModal = this.closeModal.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }
  getValidationState() {
    const length = this.state.value.length;
    if (length > 10) return "success";
    else if (length > 5) return "warning";
    else if (length > 0) return "error";
    return null;
  }

  handleChange(e) {
    this.setState({ value: e.target.value });
  }

  getStartDate() {
    return new Date("2018-12-01");
  }

  getEndDate() {
    return new Date("2018-12-31");
  }

  getValues() {
    return this.getRange(31).map(index => {
      return {
        date: this.shiftDate(today, index),
        count: 0
      };
    });
  }

  updateValue(curValue) {
    this.setState({ show: true });
  }

  shiftDate(date, numDays) {
    const newDate = new Date(date);
    newDate.setDate(newDate.getDate() + numDays);
    return newDate;
  }

  getRange(count) {
    return Array.from({ length: count }, (_, i) => i);
  }

  getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  openModal() {
    this.setState({ show: true });
  }

  closeModal() {
    this.setState({ show: false });
  }

  componentDidMount() {}

  render() {
    return (
      <div className="mainContainer">
        <Grid>
          <Row className="show-grid">
            <Col sm={12} md={12}>
              <div className="calendar-container">
                <CalendarHeatmap
                  startDate={this.state.startDate}
                  endDate={this.state.endDate}
                  values={this.state.values}
                  showOutOfRangeDays={true}
                  classForValue={value => {
                    if (!value) {
                      return "color-null";
                    } else if (value.count == 0) {
                      return "color-empty";
                    }
                    return `color-gitlab-${value.count}`;
                  }}
                  tooltipDataAttrs={value => {}}
                  onClick={value => this.updateValue(value)}
                />
              </div>
              <ReactTooltip />

              <Modal open={this.state.show} onClose={this.closeModal} center>
                <h2>Simple centered modal</h2>
                <p>
                  <form>
                    <FormGroup
                      controlId="formBasicText"
                      validationState={this.getValidationState()}
                    >
                      <ControlLabel>
                        Working example with validation
                      </ControlLabel>
                      <FormControl
                        type="text"
                        value={this.state.value}
                        placeholder="Enter text"
                        onChange={this.handleChange}
                      />
                      <FormControl.Feedback />
                      <HelpBlock>
                        Validation is based on string length.
                      </HelpBlock>
                    </FormGroup>
                  </form>
                </p>
              </Modal>
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Calendar;
