import React, { Component } from "react";
import { Grid, Row, Col, Modal, Button } from "react-bootstrap";
import CalendarHeatmap from "react-calendar-heatmap";
import ReactTooltip from "react-tooltip";

import "./../assets/Main.css";

const today = new Date();

class Calendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      show: false,
      startDate: this.getStartDate(),
      endDate: this.getEndDate(),
      values: this.getValues()
    };
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }
  getStartDate() {
    return new Date("2018-12-01");
  }

  getEndDate() {
    return new Date("2018-12-31");
  }

  getValues() {
    return this.getRange(31).map(index => {
      return {
        date: this.shiftDate(today, index),
        count: 0
      };
    });
  }

  updateValue(curValue) {
    this.handleShow();
  }

  shiftDate(date, numDays) {
    const newDate = new Date(date);
    newDate.setDate(newDate.getDate() + numDays);
    return newDate;
  }

  getRange(count) {
    return Array.from({ length: count }, (_, i) => i);
  }

  getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  componentDidMount() {}

  render() {
    return (
      <div className="mainContainer">
        <Grid>
          <Row className="show-grid">
            <Col sm={12} md={12}>
              <div className="calendar-container">
                <CalendarHeatmap
                  startDate={this.state.startDate}
                  endDate={this.state.endDate}
                  values={this.state.values}
                  showOutOfRangeDays={true}
                  classForValue={value => {
                    if (!value) {
                      return "color-null";
                    } else if (value.count == 0) {
                      return "color-empty";
                    }
                    return `color-gitlab-${value.count}`;
                  }}
                  tooltipDataAttrs={value => {}}
                  onClick={value => this.updateValue(value)}
                />
              </div>
              <ReactTooltip />
              <Button
                bsStyle="primary"
                bsSize="large"
                onClick={this.handleShow}
              >
                Launch demo modal
              </Button>
              <Modal
                className="heroModal"
                show={this.state.show}
                onHide={this.handleClose}
              >
                <Modal.Body className="heroContent">
                  <div className="modalSubHeading">
                    Select a Superhero to show their movies!
                  </div>
                  <p className="smallText">Note: Includes cameo appearances!</p>
                  <br />

                  <div className="superhero-logos">
                    <img
                      className="superhero"
                      src="Images/Icons/ironman.png"
                      style={
                        this.state.heros.has("ironman")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="ironman"
                      alt="Iron Man"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/cap.png"
                      style={
                        this.state.heros.has("cap") ? fullOpacity : halfOpacity
                      }
                      id="cap"
                      alt="Captain America"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/thor.png"
                      style={
                        this.state.heros.has("thor") ? fullOpacity : halfOpacity
                      }
                      id="thor"
                      alt="Thor"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/hulk.png"
                      style={
                        this.state.heros.has("hulk") ? fullOpacity : halfOpacity
                      }
                      id="hulk"
                      alt="Hulk"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/hawkeye.png"
                      style={
                        this.state.heros.has("hawkeye")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="hawkeye"
                      alt="Hawkeye"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/blackwidow.png"
                      style={
                        this.state.heros.has("blackwidow")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="blackwidow"
                      alt="Black Widow"
                      onClick={this.heroSelected}
                    />

                    <img
                      className="superhero"
                      src="Images/Icons/warmachine.png"
                      style={
                        this.state.heros.has("warmachine")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="warmachine"
                      alt="War Machine"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/falcon.png"
                      style={
                        this.state.heros.has("falcon")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="falcon"
                      alt="Falcon"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/loki.png"
                      style={
                        this.state.heros.has("loki") ? fullOpacity : halfOpacity
                      }
                      id="loki"
                      alt="Loki"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/bucky.png"
                      style={
                        this.state.heros.has("bucky")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="bucky"
                      alt="Bucky Barnes"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/scarletwitch.png"
                      style={
                        this.state.heros.has("scarletwitch")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="scarletwitch"
                      alt="Scarlet Witch"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/vision.png"
                      style={
                        this.state.heros.has("vision")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="vision"
                      alt="Vision"
                      onClick={this.heroSelected}
                    />

                    <img
                      className="superhero"
                      src="Images/Icons/doctorstrange.png"
                      style={
                        this.state.heros.has("doctorstrange")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="doctorstrange"
                      alt="Doctor Strange"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/spiderman.png"
                      style={
                        this.state.heros.has("spiderman")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="spiderman"
                      alt="Spiderman"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/guardians.png"
                      style={
                        this.state.heros.has("guardians")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="guardians"
                      alt="Guardians"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/antman.png"
                      style={
                        this.state.heros.has("antman")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="antman"
                      alt="Ant Man"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/wasp.png"
                      style={
                        this.state.heros.has("wasp") ? fullOpacity : halfOpacity
                      }
                      id="wasp"
                      alt="Wasp"
                      onClick={this.heroSelected}
                    />
                    <img
                      className="superhero"
                      src="Images/Icons/blackpanther.png"
                      style={
                        this.state.heros.has("blackpanther")
                          ? fullOpacity
                          : halfOpacity
                      }
                      id="blackpanther"
                      alt="Black Panther"
                      onClick={this.heroSelected}
                    />
                  </div>
                  <br />
                  <span className="selectBtns" onClick={this.selectAll}>
                    <span className="selectUnderline">Select All</span>
                  </span>
                  <span className="selectBtns" onClick={this.deselectAll}>
                    <span className="selectUnderline">Deselect All</span>
                  </span>

                  <div>Press anywhere outside to close!</div>
                </Modal.Body>
              </Modal>{" "}
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Calendar;
