import React, { Component } from "react";
import {Grid, Glyphicon, Modal, OverlayTrigger, ButtonToolbar, ToggleButtonGroup, ToggleButton, Row, Col} from 'react-bootstrap';
import CalendarHeatmap from "react-calendar-heatmap";
import ReactTooltip from "react-tooltip";

import "./../assets/Main.css";

const today = new Date();

class Calendar extends Component {
  constructor(props) {
    super(props);
    this.state = {
      open: false,
      startDate: this.getStartDate(),
      endDate: this.getEndDate(),
      values: this.getValues()
    };
    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
  }
  getStartDate() {
    return new Date("2018-12-01");
  }

  getEndDate() {
    return new Date("2018-12-31");
  }

  getValues() {
    return this.getRange(31).map(index => {
      return {
        date: this.shiftDate(today, index),
        count: 0
      };
    });
  }

  updateValue(curValue) {
    this.handleShow();
  }

  shiftDate(date, numDays) {
    const newDate = new Date(date);
    newDate.setDate(newDate.getDate() + numDays);
    return newDate;
  }

  getRange(count) {
    return Array.from({ length: count }, (_, i) => i);
  }

  getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  componentDidMount() {}

  render() {
    return (
      <div className="mainContainer">
        <Grid>
          <Row className="show-grid">
            <Col sm={12} md={12}>
              <div className="calendar-container">
              </div>
              <Button
                bsStyle="primary"
                bsSize="large"
                onClick={this.handleShow}
              >
                Launch demo modal
              </Button>
              <Modal
                show={this.state.show}
                onHide={this.handleClose}
              >
                <Modal.Body>


                  <div>Press anywhere outside to close!</div>
                </Modal.Body>
              </Modal>
            </Col>
          </Row>
        </Grid>
      </div>
    );
  }
}

export default Calendar;
